  import React, { useState } from 'react';
  import PropTypes from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';

const items = [
  {
    link:"https://stepheninfanto.github.io/",
    ImageSrc:"https://www.goalcast.com/wp-content/uploads/2017/12/Elon-musk-on-hate.jpg",
     title:"Tribute Page"          
    },
    {
      link:"https://codepen.io/stephen-infanto/pen/KKNPrmJ?editors=0100",
      ImageSrc:"https://i01.appmifile.com/webfile/globalimg/products/pc/mi-mix-alpha/index_01.jpg" ,
       title: "My Phone website"    
      },
    
        {
            link:"https://codepen.io/stephen-infanto/pen/WNGLXzz",
            ImageSrc:"https://www.builtinaustin.com/sites/www.builtinaustin.com/files/outboundengine.jpg",
             title: " Employee Work Preference Survey Form",
         },
  
         {
            link:"https://codepen.io/stephen-infanto/pen/RwoaPQQ",
            ImageSrc:"https://cdn.dribbble.com/users/461622/screenshots/10677181/media/3398f89271dc30f09d2f15dc5d31f809.png?compress=1&resize=800x600",
            title:"Technical documentation Page",
            },
];




const projectData=[
    
  {
  link:"https://stepheninfanto.github.io/",
  ImageSrc:"https://www.goalcast.com/wp-content/uploads/2017/12/Elon-musk-on-hate.jpg",
   title:"Tribute Page"          
  },
  {
    link:"https://codepen.io/stephen-infanto/pen/KKNPrmJ?editors=0100",
    ImageSrc:"https://i01.appmifile.com/webfile/globalimg/products/pc/mi-mix-alpha/index_01.jpg" ,
     title: "My Phone website"    
    },
  
      {
          link:"https://codepen.io/stephen-infanto/pen/WNGLXzz",
          ImageSrc:"https://www.builtinaustin.com/sites/www.builtinaustin.com/files/outboundengine.jpg",
           title: " Employee Work Preference Survey Form",
       },

       {
          link:"https://codepen.io/stephen-infanto/pen/RwoaPQQ",
          ImageSrc:"https://cdn.dribbble.com/users/461622/screenshots/10677181/media/3398f89271dc30f09d2f15dc5d31f809.png?compress=1&resize=800x600",
          title:"Technical documentation Page",
          },

  ];


const Work = (props) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  }

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  }

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  }

  const slides = items.map((item) => {
    return (
      <CarouselItem
        onExiting={() => setAnimating(true)}
        onExited={() => setAnimating(false)}
        key={item.ImageSrc}
      >
        <img src={item.ImageSrc} alt={item.title} />
        <CarouselCaption captionText={item.title} captionHeader={item.title} />
      </CarouselItem>
    );
  });

  return (
    <Carousel
      activeIndex={activeIndex}
      next={next}
      previous={previous}
    >
      <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={goToIndex} />
      {slides}
      <CarouselControl direction="prev" directionText="Previous" onClickHandler={previous} />
      <CarouselControl direction="next" directionText="Next" onClickHandler={next} />
    </Carousel>
  );
}

export default Work;


Carousel.propTypes = {
  // the current active slide of the carousel
  activeIndex: PropTypes.number,
  // a function which should advance the carousel to the next slide (via activeIndex)
  next: PropTypes.func.isRequired,
  // a function which should advance the carousel to the previous slide (via activeIndex)
  previous: PropTypes.func.isRequired,
  // controls if the left and right arrow keys should control the carousel
  keyboard: PropTypes.bool,
  /* If set to "hover", pauses the cycling of the carousel on mouseenter and resumes the cycling of the carousel on
   * mouseleave. If set to false, hovering over the carousel won't pause it. (default: "hover")
   */
  pause: PropTypes.oneOf(['hover', false]),
  // Autoplays the carousel after the user manually cycles the first item. If "carousel", autoplays the carousel on load.
  // This is how bootstrap defines it... I would prefer a bool named autoplay or something...
  ride: PropTypes.oneOf(['carousel']),
  // the interval at which the carousel automatically cycles (default: 5000)
  // If set to false, carousel will not Autoplay (i.e. will not automatically cycle).
  interval: PropTypes.oneOfType([
    PropTypes.number,
    PropTypes.string,
    PropTypes.bool,
  ]),
  children: PropTypes.array,
  // called when the mouse enters the Carousel
  mouseEnter: PropTypes.func,
  // called when the mouse exits the Carousel
  mouseLeave: PropTypes.func,
  // controls whether the slide animation on the Carousel works or not
  slide: PropTypes.bool,
  cssModule: PropTypes.object,
  // controls whether the touch gestures on the Carousel works or not (default: true)
  enableTouch: PropTypes.bool,
};


CarouselItem.propTypes = {
  ...Transition.propTypes,
  tag: PropTypes.oneOfType([PropTypes.func, PropTypes.string]),
  in: PropTypes.bool,
  cssModule: PropTypes.object,
  children: PropTypes.node,
  slide: PropTypes.bool,
};


CarouselControl.propTypes = {
  direction: PropTypes.oneOf(['prev', 'next']).isRequired,
  onClickHandler: PropTypes.func.isRequired,
  cssModule: PropTypes.object,
  directionText: PropTypes.string
};



CarouselIndicators.propTypes = {
  items: PropTypes.array.isRequired,
  activeIndex: PropTypes.number.isRequired,
  cssModule: PropTypes.object,
  onClickHandler: PropTypes.func.isRequired
};



CarouselCaption.propTypes = {
  captionHeader: PropTypes.node,
  captionText: PropTypes.node.isRequired,
  cssModule: PropTypes.object
};
