import React, { useState } from 'react';
import {
  Carousel,
  CarouselItem,
  CarouselControl,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';
const projectData=[
    
  {
  link:"https://stepheninfanto.github.io/",
  ImageSrc:"https://www.goalcast.com/wp-content/uploads/2017/12/Elon-musk-on-hate.jpg",
   title:"Tribute Page"          
  },
  {
    link:"https://codepen.io/stephen-infanto/pen/KKNPrmJ?editors=0100",
    ImageSrc:"https://i01.appmifile.com/webfile/globalimg/products/pc/mi-mix-alpha/index_01.jpg" ,
     title: "My Phone website"    
    },
  
      {
          link:"https://codepen.io/stephen-infanto/pen/WNGLXzz",
          ImageSrc:"https://www.builtinaustin.com/sites/www.builtinaustin.com/files/outboundengine.jpg",
           title: " Employee Work Preference Survey Form",
       },

       {
          link:"https://codepen.io/stephen-infanto/pen/RwoaPQQ",
          ImageSrc:"https://cdn.dribbble.com/users/461622/screenshots/10677181/media/3398f89271dc30f09d2f15dc5d31f809.png?compress=1&resize=800x600",
          title:"Technical documentation Page",
          },

  ];



const items = [
  {
    id: 1,
    altText: 'Slide 1',
    caption: 'Slide 1'
  },
  {
    id: 2,
    altText: 'Slide 2',
    caption: 'Slide 2'
  },
  {
    id: 3,
    altText: 'Slide 3',
    caption: 'Slide 3'
  }
];

const Work = (props) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  }

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  }

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  }

  // const slides = items.map((item) => {
  //   return (
  //     <CarouselItem
  //       className="custom-tag"
  //       tag="div"
  //       key={item.id}
  //       onExiting={() => setAnimating(true)}
  //       onExited={() => setAnimating(false)}
  //     >
  //       <CarouselCaption className="text-danger" captionText={item.caption} captionHeader={item.caption} />
  //     </CarouselItem>
  //   );
  // });


  const slides = projectData.map((item,id) => {
    return (
      <CarouselItem
        className="custom-tag"
        tag="div"
        key={id}
        onExiting={() => setAnimating(true)}
        onExited={() => setAnimating(false)}
      >
        <CarouselCaption className="text-danger " captionText={item.title} captionHeader={item} />
      </CarouselItem>
    );
  });




  return (
    <div>
      <style>
        {
          `.custom-tag {
              max-width: 100%;
              height: 500px;
              background: black;
              font-size:30px
            }`
        }
      </style>
      <Carousel
        activeIndex={activeIndex}
        next={next}
        previous={previous}
      >
        <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={goToIndex} />
        {slides}
        <CarouselControl direction="prev" directionText="Previous" onClickHandler={previous} />
        <CarouselControl direction="next" directionText="Next" onClickHandler={next} />
      </Carousel>
    </div>
  );
}

export default Work;
